(()=>{"use strict";(e=>{const t=document.getElementById("timer-hours"),n=document.getElementById("timer-minutes"),o=document.getElementById("timer-seconds");let s=setInterval((()=>{let e=(()=>{let e=(new Date("00:00:00 23 december 2021").getTime()-(new Date).getTime())/1e3;return{timeRemaining:e,hours:Math.floor(e/60/60),minutes:Math.floor(e/60%60),seconds:Math.floor(e%60)}})();console.log(e),t.textContent=e.hours<10?"0"+e.hours:e.hours,n.textContent=e.minutes<10?"0"+e.minutes:e.minutes,o.textContent=e.seconds<10?"0"+e.seconds:e.seconds,(0===e.timeRemaining||e.timeRemaining<0)&&(clearInterval(s),t.textContent="00",n.textContent="00",o.textContent="00")}),1e3)})()})();